import subprocess
import os
import sys
import time

def task_func(script_path, wait=True, *args):
    if not os.path.exists(script_path):
        raise ValueError("Script does not exist")
    try:
        result = subprocess.run([script_path] + list(args), capture_output=True, text=True, check=True)
        if wait:
            return result.returncode
        return None
    except subprocess.CalledProcessError as e:
        raise subprocess.CalledProcessError(e.returncode, e.cmd, e.stdout, e.stderr)
